import React, { useState, useEffect } from 'react'
import { supabase } from '../components/Database.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';
import './forum.css'

const db = supabase

const CreatePost = ({ session }) => {

    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')
    const [image, setImage] = useState('')
    const [category, setCategory] = useState('')

    const [successAlert, setSuccessAlert] = useState('')

    const [bodyError, setBodyError] = useState('')
    const [titleError, setTitleError] = useState('')
    const [categoryError, setCategoryError] = useState('')

    const handleSubmit = async (e) => {
        e.preventDefault()
        if (category === '') {
            setCategoryError('Perfavore seleziona una categoria')
            setBodyError('')
            setTitleError('')
            return
        } else if (title === '') {
            setTitleError('Perfavore inserisci un titolo')
            setCategoryError('')
            setBodyError('')
            return
        } else if (body === '') {
            setBodyError('Perfavore inserisci una descrizione')
            setCategoryError('')
            setTitleError('')
            return
        } else {
            setTitleError('')
            setBodyError('')
            setCategoryError('')
        }

        const imageUrl = await uploadImage(image)

        await db.from('posts').insert({ title: title, content: body, category: category })

        setTitle('')
        setBody('')
        setCategory('')
        setImage('')
        setSuccessAlert('Post creato con successo!')
    }

    async function uploadImage(){
        try {
            setUploading(true)
      
            if (!event.target.files || event.target.files.length === 0) {
              throw new Error('You must select an image to upload.')
            }
      
            const file = event.target.files[0]
            const fileExt = file.name.split('.').pop()
            const fileName = `${Math.random()}.${fileExt}`
            const filePath = `${fileName}`
      
            let { error: uploadError } = await supabase.storage.from('posts').upload(filePath, file)
      
            if (uploadError) {
              throw uploadError
            }
      
            onUpload(filePath)
          } catch (error) {
            alert(error.message)
          } finally {
            setUploading(false)
          }
    }

    const handleImageChange = (e) => {
        setImage(e.target.files[0])
    }

    setTimeout(() => setSuccessAlert(''), 10000)

    return (
        <div className="container">
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Titolo</label>
                    <input type="text" className="form-control" value={title} onChange={e => setTitle(e.target.value)} />
                    {titleError && <div className="text-danger">{titleError}</div>}
                </div>
                <div className="form-group">
                    <label>Descrizione</label>
                    <textarea className="form-control" value={body} onChange={e => setBody(e.target.value)} />
                    {bodyError && <div className="text-danger">{bodyError}</div>}
                </div>
                <div className="form-group">
                    <label>Categoria</label>
                    <select className="form-control" value={category} onChange={e => setCategory(e.target.value)}>
                        <option value="">--Select--</option>
                        <option value="Animali">Animali</option>
                        <option value="Ambiente">Ambiente</option>
                        <option value="Attualità">Attualità</option>
                    </select>
                    {categoryError && <div className="text-danger">{categoryError}</div>}
                </div>
                <br></br>
                <div className="form-group">
                    <label>Immagine</label><br></br>
                    <input type="file" className="form-control-file" onChange={handleImageChange} />
                    {successAlert && <div className="alert alert-success">{successAlert}</div>}

                </div>


                <br></br>
                <div class='line'>
                <button type="submit" className="btn btn-success">Crea Post</button>
                <br></br>
                <Link to ='/userPages/Forum'><button type="submit" className="btn btn-danger">Indietro</button></Link>
                </div>
            </form>
        </div>
    )
}
export default CreatePost
