import React, { useState, useEffect } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { supabase } from '../components/Database.js';

export default function Forum() {
    const [posts, setPosts] = useState([])
    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')

    useEffect(() => {
        const fetchData = async () => {
            const posts = await supabase.from('posts').select('*').orderBy('created_at', 'desc')
            setPosts(posts)
        }
        fetchData()
    }, [])

    const handleSubmit = async (e) => {
        e.preventDefault()
        const newPost = await supabase.from('posts').insert({ title, body })
        setTitle('')
        setBody('')
      }

    return (
        <div className="container">
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Title</label>
                    <input type="text" className="form-control" value={title} onChange={e => setTitle(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Body</label>
                    <textarea className="form-control" value={body} onChange={e => setBody(e.target.value)} />
                </div><br></br>
                <button type="submit" className="btn btn-primary">Create Post</button>
            </form>

            {posts.map(post => (
                <div className="card" style={{ margin: '20px 0' }}>
                    <div className="card-body">
                        <div key={post.id}>
                            <h2 className="card-title">{post.title}</h2>
                            <p className="card-text">{post.body}</p>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    )

}
