import React from 'react';
import './modal.css'

export default function Modal({ children, isOpen, onClose }) {
  return (
    <>
      {isOpen && (
        <div className="modal-backdrop">
          <div className="modal-window">
            
            {children}
            <br></br>
            <button onClick={onClose} className='btn btn-danger'>Close</button>
          </div>
        </div>
      )}
    </>
  );
}
