import React, { useState, useEffect } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Forum(){
    const [posts, setPosts] = useState([])

    useEffect(() => {
        const fetchData = async () => {
          const posts = await supabase.from('posts').select('*').orderBy('created_at', 'desc')
          setPosts(posts)
        }
        fetchData()
      }, [])

      return(
        <div>
        { posts.map(post => (
          <div key={post.id}>
            <h2>{post.title}</h2>
            <p>{post.body}</p>
          </div>
        ))}
      </div>
      )
    
}
