import React, { useState, useEffect } from 'react';
import Header from './Header.js';
import Figure from './Figure.js';
import WrongLetters from './WrongLetters.js';
import Word from './Word.js';
import Popup from './Popup.js';
import Notification from './Notification.js';
import { showNotification as show, checkWin } from './helpers.js';
import '../Style/impiccato.css';
import { supabase } from '../../../components/Database.js';

const db = supabase;

function Impiccato({ session }) {
  const [words, setWords] = useState([]);
  const [selectedWord, setRandomWord] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getProfile()
  }, [session])

  const getProfile = async () => {
    try {
      setLoading(true)
      const { user } = session

      let { data, error, status } = await supabase
        .from('users')
        .select(`email`)
        .eq('id', user.id)
        .single()

      if (error && status !== 406) {
        throw error
      }
    } catch (error) {
      alert(error.message)
    } finally {
      setLoading(false)
    }
  }
  
  useEffect(() => {
    const fetchWords = async () => {
      try {
        const response = await db.from('animals').select();
        setWords(response.data);
        setLoading(false);
      } catch (error) {
        console.error(error);
      }
    }
    fetchWords();
  }, []);
 
   useEffect(() => {
    if (!loading && words.length > 0) {
      const randomIndex = Math.floor(Math.random() * words.length);
      setRandomWord(words[randomIndex].Nome.toLowerCase());
    }
   }, [words, loading]);


  const [playable, setPlayable] = useState(true);
  const [correctLetters, setCorrectLetters] = useState([]);
  const [wrongLetters, setWrongLetters] = useState([]);
  const [showNotification, setShowNotification] = useState(false);

  useEffect(() => {
    
    const handleKeydown = event => {
      const { key, keyCode } = event;
      if (playable && keyCode >= 65 && keyCode <= 90) {
        const letter = key.toLowerCase();
        if (selectedWord.includes(letter)) {
          if (!correctLetters.includes(letter)) {
            setCorrectLetters(currentLetters => [...currentLetters, letter]);
          } else {
            show(setShowNotification);
          }
        } else {
          if (!wrongLetters.includes(letter)) {
            setWrongLetters(currentLetters => [...currentLetters, letter]);
          } else {
            show(setShowNotification);
          }
        }
      }
    }
    window.addEventListener('keydown', handleKeydown);

    return () => window.removeEventListener('keydown', handleKeydown);
  }, [correctLetters, wrongLetters, playable]);

  function playAgain() {
    setPlayable(true);
    setCorrectLetters([]);
    setWrongLetters([]);

    if (!loading && words.length > 0) {
      const randomIndex = Math.floor(Math.random() * words.length);
      setRandomWord(words[randomIndex].Nome.toLowerCase());
    }
  }

  return (
    <div>
    
      <Header />
      <div className="game-container">
        <Figure wrongLetters={wrongLetters} />
        <WrongLetters wrongLetters={wrongLetters} />
        <Word selectedWord={selectedWord} correctLetters={correctLetters} />
      </div>
      <Popup correctLetters={correctLetters} wrongLetters={wrongLetters} selectedWord={selectedWord} setPlayable={setPlayable} playAgain={playAgain} />
      <Notification showNotification={showNotification} />
      </div>
  );
}


export default Impiccato;
