import { supabase } from '../components/Database.js'
import { useEffect, useState } from 'react'

export default function ForumImage(url, id, size) {
    const [postUrl, setPostUrl] = useState(null)
    const [uploading, setUploading] = useState(false)

    useEffect(() => {
        if (url) downloadImage(url)
    }, [url])

    async function downloadImage(path) {
        try {
            console.log(path);
            const { data, error } = await supabase.storage.from('posts').download(path)
            if (error) {
                throw error
            }
            const url = URL.createObjectURL(data)
            setPostUrl(url)
            return url
        } catch (error) {
            console.log('Error downloading image: ', error.message)
        }
    }

    return (

        <div style={{ width: size }} aria-live="polite">
            <br></br>
            <img
                src={postUrl ? postUrl : `https://place-hold.it/${size}x${size}`}
                alt={postUrl ? 'Post' : 'No image'}
                className="post image"
                style={{ height: size, width: size }}
            />
            {uploading ? (
                'Uploading...'
            ) : (
                <><br></br><br></br>
                    <label className="c3-wait" htmlFor="single">Carica Immagine</label>
                    <div className="visually-hidden">
                        <input
                            type="file"
                            id="single"
                            accept="image/*"
                            disabled={uploading}
                        />
                    </div>
                </>
            )}
        </div>
    )
}
