import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { supabase } from './Database.js'
import { Auth,ThemeSupa } from '@supabase/auth-ui-react'


export default function LoginSupabase() {
  const [loading, setLoading] = useState(false)
  const [email, setEmail] = useState('')

  const navigate = useNavigate();

  supabase.auth.onAuthStateChange(async(event => {
    if(event!=="SIGNED_OUT"){
      navigate("../userPages/Dashboard");
    }else{
      navigate("/LoginSupabase");
    }
  }))


  const handleLogin = async (e) => {
    e.preventDefault()

    try {
      setLoading(true)
      const { error } = await supabase.auth.signInWithOtp({ email,
        
        options: {
          emailRedirectTo: 'http://localhost:3000/userPages/Dashboard/',
        }, })
      if (error) throw error
      alert('Perfavore conferma il tuo account tramite il link che hai ricevuto per mail!')
    } catch (error) {
      alert(error.error_description || error.message)
    } finally {
      setLoading(false)
    }
  }

  return (

    <div className="row flex-center flex">
      <div className="col-6 form-widget" aria-live="polite">
      <Auth
      supabaseClient={supabase}
      appearance ={{theme:ThemeSupa}}
      theme="dark"
        />
        
      </div>
    </div>
  )
}

