import React, { useEffect } from 'react';
import { checkWin } from './helpers.js';
import { supabase } from '../../../components/Database.js';

const db = supabase;

const Popup = ({correctLetters, wrongLetters, selectedWord, setPlayable, playAgain, score, id}) => {
  let finalMessage = '';
  let finalMessageRevealWord = '';
  let playable = true;

  if( checkWin(correctLetters, wrongLetters, selectedWord) === 'win' ) {
    finalMessage = 'Congratulazioni! Hai vinto! ðŸ˜ƒ';
    playable = false;
    score += 10;
    db.from('users').update({score},'score').eq('id',{id});
  } else if( checkWin(correctLetters, wrongLetters, selectedWord) === 'lose' ) {
    finalMessage = 'Sfortunatamente non hai indovinato ðŸ˜•';
    finalMessageRevealWord = `...la parola era: ${selectedWord}`;
    playable = false;
    score -= 10;
    db.from('users').update({score},'score').eq('id',{id});
  }

  useEffect(() => {
    setPlayable(playable);
  });

  return (
    <div className="popup-container" style={finalMessage !== '' ? {display:'flex'} : {}}>
      <div className="popup">
        <h2>{finalMessage}</h2>
        <h3>{finalMessageRevealWord}</h3>
        <button onClick={playAgain}>Play Again</button>
      </div>
    </div>
  )
}

export default Popup
