const CreatePost = ({ session }) => {
const handleSubmit = async (e) => {
    e.preventDefault()
    if (category === '') {
        setCategoryError('Perfavore seleziona una categoria')
        setBodyError('')
        setTitleError('')
        return
    } else if (title === '') {
        setTitleError('Perfavore inserisci un titolo')
        setCategoryError('')
        setBodyError('')
        return
    } else if (body === '') {
        setBodyError('Perfavore inserisci una descrizione')
        setCategoryError('')
        setTitleError('')
        return
    } else {
        setTitleError('')
        setBodyError('')
        setCategoryError('')
    }

    await supabase.from('posts').insert({ title: title,content: body,category: category })

    setTitle('')
    setBody('')
    setCategory('')
    setImage('')
    setSuccessAlert('Post creato con successo!')
}
}
export default CreatePost
